import { Frame, useAnimation } from 'framer';

export function App() {
  const controls = useAnimation();

  return (
    <Frame
      animate={controls}
      onTap={() => controls.start({ scale: 1.2 })}
    >
      Click me!
    </Frame>
  );
}

