const tasks = ['Task A', 'Task B', 'Task C'];

function delegateWork(task) {
    console.log('Delegating ' + task + ' to team member.');
}

tasks.forEach(task => {
    delegateWork(task);
});

const employees = ["Alice", "Bob", "Charlie"]; 
function manageEmployees(employees) { 
    employees.forEach(employee => { 
        console.log(`Managing employee: ${employee}`); 
    }); 
} 
manageEmployees(employees);

function connectOutcomesToIndividuals(outcomes, individuals) { 
  individuals.forEach(individual => { 
    console.log(`${individual} is essential for achieving ${outcomes}`); 
  }); 
}

