const revenue = 1000000;
const expenses = 600000;
const profit = revenue - expenses;
console.log('Profit:', profit);

const kpis = ['userEngagement', 'conversionRates', 'customerFeedback'];

function discoverFeatures(product) {
    let featuresToDevelop = [];
    for (let kpi of kpis) {
        if (product.metrics[kpi] > product.thresholds[kpi]) {
            featuresToDevelop.push(`Improve ${kpi} for better performance`);
        }
    }
    return featuresToDevelop;
}
